var title_f19_62_20460="Treatment of reflex syncope";
var content_f19_62_20460=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F67864&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F67864&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of reflex syncope",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Recommendations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Class*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Level",
"        <span class=\"small\">",
"         &bull;",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Explanation of the diagnosis, provision of reassurance, and explanation of risk recurrence are indicated in all patients",
"       </td>",
"       <td>",
"        I",
"       </td>",
"       <td>",
"        C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Isometric PCMs are indicated in patients with prodrome",
"       </td>",
"       <td>",
"        I",
"       </td>",
"       <td>",
"        B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiac pacing should be considered in patients with dominant cardioinhibitory CSS",
"       </td>",
"       <td>",
"        IIa",
"       </td>",
"       <td>",
"        B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiac pacing should be considered in patients with frequent recurrent reflex syncope, age &gt;40 years, and documented spontaneous cardioinhibitory response during monitoring",
"       </td>",
"       <td>",
"        IIa",
"       </td>",
"       <td>",
"        B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Midodrine may be indicated in patients with VVS refractory to lifestyle measures",
"       </td>",
"       <td>",
"        IIb",
"       </td>",
"       <td>",
"        B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tilt training may be useful for education of patients but long-term benefit depends on compliance",
"       </td>",
"       <td>",
"        IIb",
"       </td>",
"       <td>",
"        B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiac pacing may be indicated in patients with tilt-induced cardioinhibitory response with recurrent frequent unpredictable syncope and age &gt;40 after alternative therapy has failed",
"       </td>",
"       <td>",
"        IIb",
"       </td>",
"       <td>",
"        C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiac pacing is not indicated in the absence of a documented cardioinhibitory reflex",
"       </td>",
"       <td>",
"        III",
"       </td>",
"       <td>",
"        C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &beta;-adrenergic blocking drugs are not indicated",
"       </td>",
"       <td>",
"        III",
"       </td>",
"       <td>",
"        A",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CSS: carotid sinus syndrome; PCM: physical isometric counterpressure manouuvre; VVS: vasovagal syncope.",
"     <br>",
"      * Class of recommendation.",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       Level of evidence.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: European Heart Rhythm Association (EHRA), Heart Failure Association (HFA), Heart Rhythm Society (HRS), et al. Guidelines for the diagnosis and management of syncope (version 2009): the Task Force for the Diagnosis and Management of Syncope of the European Society of Cardiology (ESC). Eur Heart J 2009; 30:2631. Copyright &copy; 2009 Oxford University Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
