" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Principles of teratology",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/7/19574/contributors\">",
