" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Capillary malformations (port wine stains): Clinical features, diagnosis, and associated syndromes",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?2/10/2217/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?2/10/2217/contributors\">",
"     Sheila Galbraith, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?2/10/2217/contributors\">",
"     Section Editor",
