var title_f2_13_2269="Katz daily living scale";
var content_f2_13_2269=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F66451&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F66451&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Katz index of independence in activities of daily living",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Activities",
"      </td>",
"      <td class=\"subtitle1\">",
"       Independence",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dependence",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3\">",
"       Points (1 or 0)",
"      </td>",
"      <td class=\"subtitle3\">",
"       <p>",
"        Points (1)",
"       </p>",
"       <p>",
"        <em>",
"         NO",
"        </em>",
"        supervision, direction, or personal assistance",
"       </p>",
"      </td>",
"      <td class=\"subtitle3\">",
"       <p>",
"        Points (0)",
"       </p>",
"       <p>",
"        <em>",
"         WITH",
"        </em>",
"        supervision, direction, personal assistance or total care",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Bathing",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (1 point)",
"       </strong>",
"       Bathes self completely or needs help in bathing only a single part of the body such as the back, genital area or disabled extremity.",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (0 points)",
"       </strong>",
"       Needs help with bathing more than one part of the body, getting in or out of the tub or shower. Requires total bathing.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       POINTS:_____",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Dressing",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (1 point)",
"       </strong>",
"       Gets clothes from closets and drawers and puts on clothes and outer garments complete with fasteners. May have help tying shoes.",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (0 points)",
"       </strong>",
"       Needs help with dressing self or needs to be completely dressed.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       POINTS:_____",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Toileting",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (1 point)",
"       </strong>",
"       Goes to toilet, gets on and off, arranges clothes, cleans genital area without help.",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (0 points)",
"       </strong>",
"       Needs help transferring to the toilet, cleaning self or uses bedpan or commode.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       POINTS:_____",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Transferring",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (1 point)",
"       </strong>",
"       Moves in and out of bed or chair unassisted. Mechanical transferring aides are acceptable.",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (0 points)",
"       </strong>",
"       Needs help in moving from bed to chair or requires a complete transfer.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       POINTS:_____",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Continence",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (1 point)",
"       </strong>",
"       Exercises complete self control over urination and defecation.",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (0 points)",
"       </strong>",
"       Is partially or totally incontinent of bowel or bladder.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       POINTS:_____",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Feeding",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (1 point)",
"       </strong>",
"       Gets food from plate into mouth without help. Preparation of food may be done by another person.",
"      </td>",
"      <td rowspan=\"2\">",
"       <strong>",
"        (0 points)",
"       </strong>",
"       Needs partial or total help with feeding or requires parenteral feeding.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       POINTS:_____",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"4\">",
"       <strong>",
"        Total points:",
"       </strong>",
"       _____",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <strong>",
"     6 points:",
"    </strong>",
"    high (patient independent).",
"    <br>",
"     <strong>",
"      0 points:",
"     </strong>",
"     low (patient very dependent).",
"     <div class=\"footnotes\">",
"     </div>",
"     <div class=\"reference\">",
"      Reproduced with permission from: Katz S, Down TD, Cash HR, Grotz RC. Progress in the development of the index of ADL. Gerontologist 1970, 10:20. Copyright &copy; 1970 Oxford University Press.",
"     </div>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
