var content_f2_15_2301=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F53273&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F53273&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of non-severe Clostridium difficile associated diarrhea in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Initial episode",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild disease: metronidazole 500 mg orally three times daily or 250 mg four times daily for 10 to 14 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe disease: vancomycin 125 mg orally four times daily for 10 to 14 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        First relapse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confirm diagnosis (see text)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If symptoms are mild, conservative management may be appropriate.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If antibiotics are needed, repeat treatment as in initial episode above. Alternative: fidaxomicin 200 mg orally twice daily for 10 days",
"        <sup>",
"         [4,5]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Second relapse",
"        <sup>",
"         [1,2]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confirm diagnosis (see text)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Tapering and pulsed oral vancomycin (below), with or without probiotics (for example, Saccharomyces boulardii 500 mg orally twice daily). The probiotics may be overlapped with the final week of the taper and continued for two additional weeks in the absence of antibiotics.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        125 mg orally four times daily for 7 to 14 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        125 mg orally twice daily for 7 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        125 mg orally once daily for 7 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        125 mg orally every other day for 7 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        125 mg orally every 3 days for 14 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Alternative: fidaxomicin 200 mg orally twice daily for 10 days",
"        <sup>",
"         [4,5]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Subsequent relapse",
"        <sup>",
"         [3,4]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confirm diagnosis (see text)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fidaxomicin 200 mg orally twice daily for 10 days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Alternative: Vancomycin 125 mg orally four times daily for 14 days, followed by rifaximin 400 mg twice daily for 14 days",
"        <sup>",
"         [6]",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        Tedesco FJ, Gordon D, Fortson WC. Approach to patients with multiple relapses of antibiotic-associated pseudomembranous colitis. Am J Gastroenterol 1985; 80:867.",
"       </li>",
"       <li>",
"        Cohen SH, Gerding DN, Johnson S, et al: Clinical practice guidelines for Clostridium difficile infection in adults: 2010 update by the society for healthcare epidemiology of America (SHEA) and the infectious diseases society of America (IDSA). Infect Control Hosp Epidemiol 2010; 31(5):431-455.",
"       </li>",
"       <li>",
"        Johnson S, Schriever C, Galang M, et al. Interruption of recurrent Clostridium difficile-associated diarrhea episodes by serial therapy with vancomycin and rifaximin. Clin Infect Dis 2007; 44:846.",
"       </li>",
"       <li>",
"        Louie TJ, et al: Fidaxomicin versus vancomycin for Clostridium difficile infection. N Engl J Med 2011; 364:422-31.",
"       </li>",
"       <li>",
"        Cornely OA, Miller MA, Louie TJ, et al. Treatment of first recurrence of Clostridium difficile infection: fidaxomicin versus vancomycin.&nbsp;Clin Infect Dis 2012; 55 Suppl 2:S154.&nbsp;&nbsp;",
"       </li>",
"       <li>",
"        Garey KW, Ghantoji SS, Shah DN, et al. A randomized, double-blind, placebo-controlled pilot study to assess the ability of rifaximin to prevent recurrent diarrhoea in patients with Clostridium difficile infection. J Antimicrob Chemother 2011; 66:2850.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
