var title_f2_16_2316="Primary prevention CHD elderly";
var content_f2_16_2316=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F60480&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F60480&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Therapeutic considerations for primary prevention of coronary heart disease in elderly patients",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Low-density lipoprotein cholesterol level mg/dL (mmol/L)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Risk factors*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Therapeutic considerations&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        130 - 159 (3.36 - 4.11)",
"       </td>",
"       <td>",
"        0 - 1",
"       </td>",
"       <td>",
"        Life-habit changes indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &ge;2",
"       </td>",
"       <td>",
"        Intensify life-habit changes; special considerations for drug therapy&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        160 - 189 (4.14 - 4.89)",
"       </td>",
"       <td>",
"        0 - 1",
"       </td>",
"       <td>",
"        Intensify life-habit changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &ge;2",
"       </td>",
"       <td>",
"        Consider adding drug therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &ge;190 (&ge;4.91)",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Consider adding drug therapy to life-habit changes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Risk factors include cigarette smoking, hypertension, low high-density lipoprotein cholesterol level, advancing age (&ge;45 years in men and &ge;55 years in women or postmenopausal), and diabetes mellitus.",
"     <br>",
"      &bull; Life-habit changes include Step I diet, weight control, and regular exercise. Intensify life-habit changes include Step II diet, medically supervised weight reduction, and exercise program.",
"      <br>",
"       &Delta; Some authorities consider cholesterol-lowering drug therapy if a patient has diabetes mellitus plus other risk factors. Drug therapy also may be appropriate if the patient is considered at very high risk from multiple risk factors or from subclinical atherosclerosis.",
"       <br>",
"        &plusmn; Indicates with or without risk factors.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from Grundy, SM, Cleeman, JI, Rifkind, BM, Kuller, LH, for the Coordinating Committee of the National Cholesterol Education Program. Arch Intern Med 1999; 159:1670.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
