var title_f2_17_2332="Anticoagulation and surgery";
var content_f2_17_2332=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F81389&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F81389&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for preoperative and postoperative anticoagulation in patients on oral anticoagulation therapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Indication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Before surgery",
"       </td>",
"       <td class=\"subtitle1\">",
"        After",
"surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Venous thromboembolism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Within first month",
"       </td>",
"       <td class=\"indent1\">",
"        IV heparin or SQ LMWH",
"       </td>",
"       <td class=\"indent1\">",
"        IV heparin or SQ LMWH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Second/third month",
"       </td>",
"       <td class=\"indent1\">",
"        No change*",
"       </td>",
"       <td class=\"indent1\">",
"        IV heparin or SQ LMWH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &ge;3",
"months",
"       </td>",
"       <td class=\"indent1\">",
"        No change*",
"       </td>",
"       <td class=\"indent1\">",
"        SQ heparin or LWMH",
"        <br/>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Arterial thromboembolism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Recent, within one month",
"       </td>",
"       <td class=\"indent1\">",
"        IV heparin or SQ LMWH",
"       </td>",
"       <td class=\"indent1\">",
"        IV heparin or SQ LMWH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prophylaxis (non valvular AF,",
"mechanical valves)",
"       </td>",
"       <td class=\"indent1\">",
"        No change*",
"       </td>",
"       <td class=\"indent1\">",
"        Resume oral anticoagulation&bull;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    NOTE: Warfarin should be withheld to allow the INR to fall spontaneously to 1.5 to 2 before surgery is performed.",
"    <div class=\"footnotes\">",
"     LMWH: low molecular weight heparin.",
"     <br>",
"      * If the patient is hospitalized, subcutaneous (SQ) heparin or LMWH should be administered, but hospitalization is not recommended solely for this purpose.",
"      <br>",
"       &bull; Can use SQ heparin or SQ LMWH if the surgery carries a high risk of postoperative thromboembolism.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
