var title_f2_17_2333="Screening tests for occlusive vascular syndromes";
var content_f2_17_2333=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F62872&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F62872&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Screening tests for occlusive vascular syndromes",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Complete blood count with differential, platelet count, and blood smear",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Polycythemia or anemia, granulocytosis, thrombocytosis, poikilocytosis",
"        <br/>",
"        <ul>",
"         <li>",
"          May be sign of myeloproliferative or myelodysplastic diseases",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evidence of red cell fragmentation (schistocytes) and microangiopathy, consider:",
"        <br/>",
"        <ul>",
"         <li>",
"          TTP-hemolytic uremic syndrome",
"         </li>",
"         <li>",
"          Purpura fulminans with DIC, occasionally",
"         </li>",
"         <li>",
"          Antiphospholipid antibody syndrome, occasionally",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thrombocytopenia, consider:",
"        <br/>",
"        <ul>",
"         <li>",
"          Heparin necrosis, some patients",
"         </li>",
"         <li>",
"          Purpura fulminans with DIC",
"         </li>",
"         <li>",
"          TTP-hemolytic uremic syndrome",
"         </li>",
"         <li>",
"          Antiphospholipid antibody syndrome, some patients",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe neutropenia",
"        <br/>",
"        <ul>",
"         <li>",
"          Immunocompromised; opportunistic infections very likely",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eosinophilia",
"        <br/>",
"        <ul>",
"         <li>",
"          Cholesterol emboli",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Partial thromboplastin time (PTT)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minimally prolonged in TTP-hemolytic uremic syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolonged in purpura fulminans and DIC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolonged if lupus anticoagulant activity present, and assay sensitive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cryoglobulins (occasionally cryofibrinogen and cold agglutinins) when location and history suggest cold occlusion syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confirms occlusion versus other mechanisms of vessel injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May suggest platelet plugs instead of fibrin thrombi",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May suggest cryoglobulin gelling",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May diagnose calciphylaxis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May suggest cholesterol, oxalate or crystal globulin occlusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Special stains for organisms in immunocompromised hosts, when appropriate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Basic hepatic and renal function screens",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hepatic function can affect vitamin K-dependent factors and warfarin (Coumadin&reg;) metabolism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal disease moves cutaneous calciphylaxis higher in the differential diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        ANCA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        To reduce the likelihood of the uncommon non-inflammatory retiform purpura presentation of granulomatosis with polyangiitis (Wegener's granulomatosis) or microscopic polyangiitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ANCA may be available before permanent histologic sections",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Cutaneous manifestations of microvascular occlusion syndromes. In: Dermatology, 2nd ed, Bolognia JL, Jorizzo JL, Rapini RP (Eds), Elsevier Limited, Spain 2008. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
