var title_f2_19_2365="ACC AHA indication stopping ETT";
var content_f2_19_2365=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F53266&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F53266&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Indications for terminating exercise testing",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\" rowspan=\"1\">",
"        Absolute indications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Drop in systolic blood pressure &gt;10 mmHg from baseline, despite an increase in workload when accompanied by any other evidence of ischemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Moderate-to-severe angina, defined as level 2 angina, approaching level 3; the angina scale is described below",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Increasing nervous system symptoms (eg, ataxia, dizziness, or near syncope)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Signs of poor perfusion (cyanosis or pallor)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Technical difficulties monitoring the ECG or systolic blood pressure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Patient's request to stop",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Sustained ventricular tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; ST segment elevation (&gt;1.0 mm) in leads without diagnostic Q waves (other than V1 or aVR)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\" rowspan=\"1\">",
"        Relative Indications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Drop in systolic blood pressure &ge;10 mmHg from baseline. despite an increase in workload in the absence of other evidence of ischemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; ST or QRS changes such as excessive ST depression (&gt;2 mm of horizontal or downsloping of ST segment depression) or marked axis shift",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Arrhythmias other than sustained ventricular tachycardia, including multifocal ventricular premature beats, ventricular triplets, supraventricular tachycardia, heart block, or bradyarrhythmias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Fatigue, shortness of breath, wheezing, leg cramps, or claudication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Development of bundle branch block or intraventricular conduction delay that cannot be distinguished from ventricular tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Increasing chest pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        &bull;&nbsp; Hypertensive response (systolic blood pressure &gt;250 mmHg and/or diastolic blood pressure &gt;115 mmHg)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Four-level angina scale for exercise tolerance testing",
"       </td>",
"       <td class=\"subtitle1\">",
"        Level",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Onset of angina, mild but recognized as the usual angina-of-effort pain or discomfort with which the patient is familiar",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Same pain, moderately severe and definitely uncomfortable but still tolerable",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe anginal pain at a level that the subject will wish to stop exercising",
"       </td>",
"       <td>",
"        3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unbearable chest pain; the most severe pain the patient has felt",
"       </td>",
"       <td>",
"        4",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Gibbons, RJ, Balady, GJ, Bricker, JT, et al. ACC/AHA 2002 guideline update for exercise testing: summary article: a report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Committee to Update the 1997 Exercise Testing Guidelines). Circulation 2002; 106:1883.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
