var content_f2_22_2413=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F53191&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F53191&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    TNM staging carcinoid tumors of the colon and rectum",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Primary tumor (T)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       TX",
"      </td>",
"      <td colspan=\"3\">",
"       Primary tumor cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T0",
"      </td>",
"      <td colspan=\"3\">",
"       No evidence of primary tumor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       T1",
"      </td>",
"      <td class=\"sublist_other_start\" colspan=\"3\">",
"       Tumor invades lamina propria or submucosa and size 2 cm or less",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T1a",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor size less than 1 cm in greatest dimension",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T1b",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor size 1-2 cm in greatest dimension",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor invades muscularis propria or size more than 2 cm with invasion of lamina propria or submucosa",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor invades through the muscularis propria into the subserosa, or into non-peritonealized pericolic or perirectal tissues",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       T4",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor invades peritoneum or other organs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"3\">",
"       For any T, add (m) for multiple tumors",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Regional lymph nodes (N)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       NX",
"      </td>",
"      <td colspan=\"3\">",
"       Regional lymph nodes cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N0",
"      </td>",
"      <td colspan=\"3\">",
"       No regional lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N1",
"      </td>",
"      <td colspan=\"3\">",
"       Regional lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Distant metastases (M)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M0",
"      </td>",
"      <td colspan=\"3\">",
"       No distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M1",
"      </td>",
"      <td colspan=\"3\">",
"       Distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Anatomic stage/prognostic groups",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage 0",
"      </td>",
"      <td>",
"       Tis",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage I",
"      </td>",
"      <td>",
"       T1",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IIA",
"      </td>",
"      <td>",
"       T2",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IIB",
"      </td>",
"      <td>",
"       T3",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IIIA",
"      </td>",
"      <td>",
"       T4",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IIIB",
"      </td>",
"      <td>",
"       Any T",
"      </td>",
"      <td>",
"       N1",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IV",
"      </td>",
"      <td>",
"       Any T",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M1",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Note: cTNM is the clinical classification, pTNM is the pathologic classification.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Used with the permission of the American Joint Committee on Cancer (AJCC), Chicago, Illinois. The original source for this material is the AJCC Cancer Staging Manual, Seventh Edition (2010) published by Springer New York, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
