var title_f2_24_2446="Blunt trauma mechanism";
var content_f2_24_2446=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F50454&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F50454&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Blunt trauma mechanisms and associated injuries",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Mechanism of injury",
"       </td>",
"       <td class=\"subtitle1\">",
"        Additional considerations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Potential associated injuries",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Motor vehicle collisions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Head-on collision",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Facial injuries",
"        </p>",
"        <p>",
"         Lower extremity injuries",
"        </p>",
"        <p>",
"         Aortic injuries",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rear-end collision",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Hyperextension injuries of cervical spine",
"        </p>",
"        <p>",
"         Cervical spine fractures",
"        </p>",
"        <p>",
"         Central cord syndrome",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lateral (T-bone) collision",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Thoracic injuries",
"        </p>",
"        <p>",
"         Abdominal injuries: spleen, liver",
"        </p>",
"        <p>",
"         Pelvic injuries",
"        </p>",
"        <p>",
"         Clavicle, humerus, rib fractures",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rollover",
"       </td>",
"       <td>",
"        <p>",
"         Greater chance of ejection",
"        </p>",
"        <p>",
"         Significant mechanism of injury",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Crush injuries",
"        </p>",
"        <p>",
"         Compression fractures of spine",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ejected from vehicle",
"       </td>",
"       <td>",
"        <p>",
"         Likely unrestrained",
"        </p>",
"        <p>",
"         Significant mortality",
"        </p>",
"       </td>",
"       <td>",
"        Spinal injuries",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Windshield damage",
"       </td>",
"       <td>",
"        Likely unrestrained",
"       </td>",
"       <td>",
"        <p>",
"         Closed head injuries, coup and countercoup injuries",
"        </p>",
"        <p>",
"         Facial fractures",
"        </p>",
"        <p>",
"         Skull fractures",
"        </p>",
"        <p>",
"         Cervical spine fractures",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Steering wheel damage",
"       </td>",
"       <td>",
"        Likely unrestrained",
"       </td>",
"       <td>",
"        Thoracic injuries",
"        <ul>",
"         <li>",
"          Sternal and rib fractures, flail chest",
"         </li>",
"         <li>",
"          Cardiac contusion",
"         </li>",
"         <li>",
"          Aortic injuries",
"         </li>",
"         <li>",
"          Hemo/pneumothoraces",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dashboard involvement/damage",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Pelvic and acetabular injuries",
"        </p>",
"        <p>",
"         Dislocated hip",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"3\">",
"        Restraint/seat belt use",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Proper three-point restraint",
"       </td>",
"       <td class=\"sublist_other\">",
"        Decreased morbidity",
"       </td>",
"       <td class=\"sublist_other\">",
"        Sternal and rib fractures, pulmonary contusions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Lap belt only",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"       <td class=\"sublist_other\">",
"        Chance fractures, abdominal injuries, head and facial injuries/fractures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Shoulder belt only",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"       <td class=\"sublist_other\">",
"        Cervical spine injuries/fractures, \"submarine\" out of restraint devices (possible ejection)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Airbag deployment",
"       </td>",
"       <td>",
"        <p>",
"         Front-end collisions",
"        </p>",
"        <p>",
"         Less severe head/upper torso injuries",
"        </p>",
"        <p>",
"         Not effective for lateral impacts",
"        </p>",
"        <p>",
"         More severe injuries in children (improper front seat placement)",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Upper extremity soft tissue injuries/fractures",
"        </p>",
"        <p>",
"         Lower extremity injuries/fractures",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Pedestrian versus automobile",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Low speed (braking automobile)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Tibia and fibula fractures, knee injuries",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        High speed",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Waddle's triad - tibia/fibula or femur fractures, truncal injuries, craniofacial injuries",
"        </p>",
"        <p>",
"         \"Thrown\" pedestrians at risk for multisystem injuries",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Bicycle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Automobile related",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Closed head injuries",
"        </p>",
"        \"Handlebar\" injuries",
"        <ul>",
"         <li>",
"          Spleen/liver lacerations",
"         </li>",
"         <li>",
"          Additional intra-abdominal injuries",
"         </li>",
"         <li>",
"          Consider penetrating injuries",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nonautomobile related",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Extremity injuries",
"        </p>",
"        <p>",
"         \"Handlebar\" injuries",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\">",
"        Falls",
"       </td>",
"       <td colspan=\"2\">",
"        LD",
"        <sub>",
"         50",
"        </sub>",
"        36 - 60 ft",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vertical impact",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Calcaneal and lower extremity fractures",
"        </p>",
"        <p>",
"         Pelvic fractures",
"        </p>",
"        <p>",
"         Closed head injuries",
"        </p>",
"        <p>",
"         Cervical spine fractures",
"        </p>",
"        <p>",
"         Renal and renal vascular injuries",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Horizontal impact",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Craniofacial fractures",
"        </p>",
"        <p>",
"         Hand and wrist fractures",
"        </p>",
"        <p>",
"         Abdominal and thoracic visceral injuries",
"        </p>",
"        <p>",
"         Aortic injuries",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Gross, E, Martel, M. Multiple trauma. In: Rosen's Emergency Medicine: Concepts and Clinical Practice, 7th ed., Marx, JA, Hockberger, RS, Walls, RM, et al. (Eds), Mosby Elsevier, Philadelphia 2010. Illustration used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
