var title_f2_29_2525="Reporting elements for ER and PR immunohistochemistry assays";
var content_f2_29_2525=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F78069&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F78069&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Reporting elements for ER and PR immunohistochemistry assays",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <span style=\"color: #008000;\">",
"         Patient identification information",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"color: #008000;\">",
"         Physician identification",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"color: #008000;\">",
"         Date of service",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"color: #008000;\">",
"         Specimen site and type",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"color: #008000;\">",
"         Specimen identification (case and block number)",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fixative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cold ischemia time (time between removal and fixation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Duration of fixation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Staining method utilized:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Primary antibody and vendor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Assay details and other reagents/vendors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        References supporting validation of assay (note: most commonly, these will be published studies performed by others that the testing laboratory is emulating)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Status of FDA approval",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Controls (high protein expression, low-level protein expression, negative protein expression, internal elements or from normal breast tissue included with sample)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adequacy of sample for evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <span style=\"color: #008000;\">",
"         Results:",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <span style=\"color: #008000;\">",
"         Percentage of invasive tumor cells exhibiting nuclear staining",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <span style=\"color: #008000;\">",
"         Intensity of staining: Strong, medium, weak",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <span style=\"color: #008000;\">",
"         Interpretation:",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        -",
"        <span style=\"color: #008000;\">",
"         Positive (for ER or PR receptor protein expression), negative (for ER or PR protein expression), uninterpretable",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        -",
"        <span style=\"color: #008000;\">",
"         Internal and external controls (positive, negative, not present)",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        -",
"        <span style=\"color: #008000;\">",
"         Standard assay conditions met/not met (including cold ischemic time and fixation parameters)",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        -",
"        <span style=\"color: #008000;\">",
"         Optional score and scoring system",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        - Comment: Should explain reason for uninterpretable result and or any other unusual conditions, if applicable. May report on status of any DCIS staining in the sample. Should also provide correlation with histologic type of the tumor. May provide information about laboratory accreditation status.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Report should contain the elements in",
"    <span style=\"color:#008000\">",
"     <strong>",
"      green",
"     </strong>",
"    </span>",
"    as a minimum. Other information must be available in the laboratory for review or appear on the patient accession slip.",
"    <div class=\"footnotes\">",
"     ER: estrogen receptor; PR: progesterone receptor.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
