" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of the infant whose mother has received group B streptococcal chemoprophylaxis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?2/31/2552/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?2/31/2552/contributors\">",
"     Karen M Puopolo, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?2/31/2552/contributors\">",
"     Carol J Baker, MD",
"    </a>",
"    <br/>",
"   </div>",
