var title_f2_31_2555="Risk of spine injury in children";
var content_f2_31_2555=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F55343&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F55343&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Factors producing increased mobility and risk of injury to the pediatric spine",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Large size of head in comparison to neck and trunk, increasing flexion, extension, and shearing forces on the cervical spine",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Underdevelopment of paraspinous muscles",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Shallow, horizontally oriented facet joints",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Increased elasticity of facet joint ligaments",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Incompletely ossified vertebrae",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       High water content and elasticity of intervertebral disks, increasing vertical loading effect",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Akbarnia, BA. Orthop Clin North Am 1999; 30:521; Thomas, NH, Robinson, L, Evans, A, Bullock, P. Pediatr Neurosurg 1995; 23:188; and Rooks, VJ, Sisler, C, Burton, B. Pediatr Radiol 1998; 28:193.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
