var title_f2_31_2556="Class snake bite US";
var content_f2_31_2556=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F53948&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F53948&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical classification of United States Crotalinae* envenomation to guide FabAV administration",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Tissue effect",
"       </td>",
"       <td class=\"subtitle1\">",
"        Systemic signs",
"       </td>",
"       <td class=\"subtitle1\">",
"        Coagulopathy and bleeding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minimal",
"       </td>",
"       <td>",
"        Swelling, pain, and ecchymosis adjacent to the bite site",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Normal coagulation parameters&bull;; no bleeding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Moderate",
"       </td>",
"       <td>",
"        Swelling, pain, and ecchymosis less than full extremity or less than 50 cm if bite on head, neck, or trunk",
"       </td>",
"       <td>",
"        Present but not life-threatening (eg, nausea, vomiting, diarrhea, oral paresthesia, unusual tastes, tachycardia, tachypnea, mild hypotension [systolic BP &gt;90 mmHg in an adult])",
"       </td>",
"       <td>",
"        Abnormal coagulation parameters&bull;; no bleeding or minor hematuria, gum bleeding, and/or epistaxis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe",
"       </td>",
"       <td>",
"        Swelling, pain, ecchymosis involving more than the entire extremity; greater than 50 cm if bite on head, neck, or trunk; threatens the airway; OR signs of compartment syndrome",
"       </td>",
"       <td>",
"        Present and life-threatening (eg, respiratory insufficiency, marked tachycardia for age with severe hypotension, obtundation, seizures)",
"       </td>",
"       <td>",
"        Markedly abnormal coagulation parameters&bull; with serious bleeding",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     FabAV: polyvalent Crotalidae ovine immune Fab (Crofab&reg;, Protherics); BP: blood pressure.",
"     <br>",
"      * Includes bites by rattlesnakes, water moccasins (cottonmouths), and copperheads.",
"      <br>",
"       &bull; Coagulation parameters include platelet count, prothrombin time (PT), partial thromboplastin time (PTT), fibrinogen, and fibrin degradation products (fibrin split products [FSP]).",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Goto, CS, Feng, SY. Crotalidae polyvalent immune fab for the treatment of pediatric crotaline envenomation. Pediatr Emerg Care 2009; 25:273.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
