var title_f2_31_2557="Antiepileptic drugs in children part II";
var content_f2_31_2557=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F80434&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F80434&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Drug therapy for seizures in children (continued)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Dose",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Blood level",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Laboratory",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Starting",
"       </td>",
"       <td class=\"subtitle2\">",
"        Maintenance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oxcarbazepine&nbsp;",
"       </td>",
"       <td>",
"        8-10 mg/kg/day&nbsp;",
"       </td>",
"       <td>",
"        30-40 mg/kg/day&nbsp;",
"       </td>",
"       <td>",
"        8-35 mcg/mL&nbsp;",
"       </td>",
"       <td>",
"        CBC/plts every 3 months for first 6 months, chemical panel prn (routine monitoring may not be necessary)&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Phenytoin, fosphenytoin (intravenous)",
"       </td>",
"       <td>",
"        5&nbsp;mg/kg/day",
"       </td>",
"       <td rowspan=\"2\">",
"        4-8 mg/kg/day (older children, adolescents 300 mg/day)",
"       </td>",
"       <td rowspan=\"2\">",
"        10-20 mcg/mL&nbsp;",
"       </td>",
"       <td rowspan=\"2\">",
"        Routine lab tests not recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Status elilepticus 20 mg/kg (fosphenytoin)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Phenobarbital",
"       </td>",
"       <td rowspan=\"2\">",
"        3-4 mg/kg/day",
"       </td>",
"       <td>",
"        Infants 5-6 mg/kg/day",
"       </td>",
"       <td rowspan=\"2\">",
"        10-40 mcg/mL",
"       </td>",
"       <td rowspan=\"2\">",
"        Routine lab tests not recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Children 1-5 y.o.&nbsp;6-8 mg/kg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Primidone",
"       </td>",
"       <td>",
"        1-2 mg/kg/day",
"       </td>",
"       <td>",
"        10-25 mg/kg/day",
"       </td>",
"       <td>",
"        5-12 mcg/mL (phenobarbital level 1.2 x higher)",
"       </td>",
"       <td>",
"        Routine lab tests not recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rufinamide&nbsp;",
"       </td>",
"       <td>",
"        10 mg/kg/day&nbsp;",
"       </td>",
"       <td>",
"        45 mg/kg/day&nbsp;",
"       </td>",
"       <td>",
"        Not established&nbsp;",
"       </td>",
"       <td>",
"        Routine lab tests not recommended&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Topiramate",
"       </td>",
"       <td>",
"        1-3 mg/kg/day",
"       </td>",
"       <td>",
"        5-9 mg/kg/day",
"       </td>",
"       <td>",
"        5-20 mcg/mL",
"       </td>",
"       <td>",
"        CBC/plts, hepatic function tests, serum K when maximum dose reached or at 3-6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Tiagibine",
"       </td>",
"       <td>",
"        &lt;12 years 0.5 mg/kg/day",
"       </td>",
"       <td>",
"        Weekly increase 0.5-1 mg/kg/day with max 4-8 mg/kg/day (3-4 divided daily doses)",
"       </td>",
"       <td rowspan=\"2\">",
"        Not established",
"       </td>",
"       <td rowspan=\"2\">",
"        Routine lab tests not recommended&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         &gt;12 years 4 mg/day",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Increase 4 mg/week to max 32 mg/day (2-4 divided daily doses)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Valproate&nbsp;",
"       </td>",
"       <td>",
"        10-15 mg/kg/day&nbsp;",
"       </td>",
"       <td>",
"        30-60 mg/kg/day&nbsp;",
"       </td>",
"       <td>",
"        50-150 mcg/mL&nbsp;",
"       </td>",
"       <td>",
"        Hepatic function tests every 1-2 months&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Zonisamide&nbsp;",
"       </td>",
"       <td>",
"        1-2 mg/kg/day&nbsp;",
"       </td>",
"       <td>",
"        5-8 mg/kg/day&nbsp;",
"       </td>",
"       <td>",
"        10-40 mcg/mL&nbsp;",
"       </td>",
"       <td>",
"        Serum electrolytes&nbsp;when maximum dose is reached or at 3-6 months&nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CBC: complete blood count; max: maximum; plts: platelets; y.o.: years old",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
