" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Association between Helicobacter pylori infection and gastrointestinal malignancy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?2/36/2634/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?2/36/2634/contributors\">",
"     Sheila E Crowe, MD, FRCPC, FACP, FACG, AGAF",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
