"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
