"  <div class=\"headingAnchor\" id=\"references\">",
"   <h1>",
"    REFERENCES",
"   </h1>",
"   <ol id=\"reference\">",
"    <li>",
"     <div class=\"reference\">",
"      American Academy of Pediatrics, Committee on Drugs and Committee on Environmental Health, &ldquo;Use of Chloral Hydrate for Sedation in Children,&rdquo;",
"      <i>",
"       Pediatrics",
"      </i>",
"      , 1993, 92(3):471-3.",
