var title_f2_39_2684="Features OD 1p19q deletion";
var content_f2_39_2684=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F79507&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F79507&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Typical features of oligodendroglial tumors with and without codeletion of chromosomes 1p/19q",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Codeletion of 1p/19q present",
"       </td>",
"       <td class=\"subtitle1\">",
"        Codeletion of 1p/19q absent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Other genetic alterations",
"       </td>",
"       <td>",
"        p16 deletions often present in anaplastic tumors",
"       </td>",
"       <td>",
"        p53 mutations, EGFR amplification, loss of 10 and 10q",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Histology features",
"       </td>",
"       <td>",
"        Classical oligodendroglial morphology",
"       </td>",
"       <td>",
"        Astrocytic elements present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Localization",
"       </td>",
"       <td>",
"        Frontal, parietal, occipital",
"       </td>",
"       <td>",
"        Temporal, deep basal ganglia, diencephalon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MRI features",
"       </td>",
"       <td>",
"        <p>",
"         Indistinct borders and mixed signal intensity on T1- and T2-weighted images",
"        </p>",
"        <p>",
"         Diffuse, patchy enhancement",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Homogeneous T1 and T2 signal intensity, distinct borders",
"        </p>",
"        <p>",
"         Ring enhancement, necrosis",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chemotherapy responsiveness",
"       </td>",
"       <td>",
"        80 to 100 percent of tumors responding, with relatively long duration of response",
"       </td>",
"       <td>",
"        Objective response less frequent and of shorter duration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Natural history",
"       </td>",
"       <td>",
"        Often present with indolent tumors and seizures only",
"       </td>",
"       <td>",
"        More rapid clinical progression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Median survival (anaplastic tumors)",
"       </td>",
"       <td>",
"        Greater than 10 years when surgery followed by combined radiation therapy plus chemotherapy",
"       </td>",
"       <td>",
"        Two to three years",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
