var script_f2_41_2706=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"function alertNaN(){",
"alert('This field is improperly formatted. You may only input the digits 0-9 and a decimal point.');  ",
"doCalc = false; ",
"clrResults();",
"}",
"",
"",
"function validNumberField(event) {",
"var field = event.target;",
"",
"if(field && field != 'undefined' && field.attributes.type.nodeValue && (field.attributes.type.nodeValue == 'number')) {",
"var val = field.value;",
"var charCode = event.charCode;",
"var key = String.fromCharCode(charCode);",
"",
"if(charCode == 0) {",
"return true;",
"}",
"",
"if(key == ' ') {",
"alert('Numeric field cannot contain spaces.');",
"return false;",
"}	",
"		",
"if(val.length == 0) {",
"if(key == '+') { return true; }",
"if(key == '-') { return true; }	",
"if(key == '.') { return true; }",
"}",
"		",
"if(key == '.') { return validFloat(val, key); }",
"",
"if(isNaN(parseInt(key))) {",
"alertNaN();",
"return false;",
"}",
"	",
"return validFloat(val, key);",
"}",
"return true;",
"}",
"",
"",
"function validFloat(val, key) {",
"var currentVal = parseFloat(val + key);",
"if(isNaN(val + key) || isNaN(currentVal)) {",
"alertNaN();",
"}",
"return true;",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function IBW_Devine_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 2000);",
"",
"with(document.IBW_Devine_form){",
"",
"",
"doCalc = true;",
"param_value = parseFloat(Height_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Height_unit.options[Height_unit.selectedIndex].value.split('|');",
"Height = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"param_value = parseFloat(Actual_Weight_param.value);",
"unit_parts = Actual_Weight_unit.options[Actual_Weight_unit.selectedIndex].value.split('|');",
"Actual_Weight = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"Ideal_Body_Weight =  (2.3 * (Height - 60)) + IBW_Sex;",
"",
"unit_parts = Ideal_Body_Weight_unit.options[Ideal_Body_Weight_unit.selectedIndex].value.split('|');",
"if (doCalc) Ideal_Body_Weight_param.value = fixDP((Ideal_Body_Weight - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"Dosing_Weight =  0.4 * (Actual_Weight - Ideal_Body_Weight) + Ideal_Body_Weight;",
"",
"unit_parts = Dosing_Weight_unit.options[Dosing_Weight_unit.selectedIndex].value.split('|');",
"if (doCalc) Dosing_Weight_param.value = fixDP((Dosing_Weight - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"if (isNaN(Dosing_Weight_param.value)) Dosing_Weight_param.value = '';",
"",
"if (Actual_Weight < (1.25 * Ideal_Body_Weight)){ Dosing_Weight_param.value = 'N/A';};",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.IBW_Devine_form){",
"",
"if (Height_param.value && isNaN(Height_param.value)){ Height_param.value = ''; alertNaN(); }",
"if (Height_param.value && Height < 60) {",
"Height = 0;",
"Height_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Height is 60 in.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Height_param.value && Height > 108) {",
"Height_param.value = \"\";",
"clrResults();",
"Height = 0;",
"doCalc = false;",
"alert(\"The maximum value for Height is 108 in.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Actual_Weight_param.value && isNaN(Actual_Weight_param.value)){ Actual_Weight_param.value = ''; alertNaN(); }",
"if (Actual_Weight_param.value && Actual_Weight < 30) {",
"Actual_Weight = 0;",
"Actual_Weight_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Actual Weight is 30 kg.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Actual_Weight_param.value && Actual_Weight > 300) {",
"Actual_Weight_param.value = \"\";",
"clrResults();",
"Actual_Weight = 0;",
"doCalc = false;",
"alert(\"The maximum value for Actual Weight is 300 kg.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.IBW_Devine_form){",
"",
"Ideal_Body_Weight_param.value = '';",
"Dosing_Weight_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"var IBW_Sex;",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
