var title_f2_41_2717="Approaches for ERCP in patients with Roux-en-Y anatomy";
var content_f2_41_2717=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F54031&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F54031&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Approaches for ERCP in patients with Roux-en-Y anatomy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Technique",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Best application",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Duodenoscope advanced transorally through anatomic route",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Ideal instrument for cannulation and therapy of native papilla",
"         </li>",
"         <li>",
"          Minimally invasive",
"         </li>",
"        </ul>",
"       </td>",
"       <td class=\"indent1\">",
"        Frequently unsuccessful due to inability to reach target",
"       </td>",
"       <td>",
"        Patients with short Roux limb and native papilla",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Colonoscope/enteroscope advanced transorally through anatomic route",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Greater depth of insertion compared to duodenoscope",
"         </li>",
"         <li>",
"          Minimally invasive",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Frequently unsuccessful in patients with long Roux limb",
"         </li>",
"         <li>",
"          Forward view",
"         </li>",
"         <li>",
"          Lack of elevator",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Patients with short Roux limb and bilioenteric/ pancreatoenteric anastomosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Deep enteroscopy assisted ERCP",
"       </td>",
"       <td class=\"indent1\">",
"        Greater reliability in reaching target, even in patients with long Roux limb",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Forward view",
"         </li>",
"         <li>",
"          Lack of elevator",
"         </li>",
"         <li>",
"          Limited availability of accessories and instruments",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Patients with long Roux limb and bilioenteric/ pancreatoenteric anastomosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Transgastrostomy tract ERCP",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Allows use of side viewing duodenoscope and all standard accessories",
"         </li>",
"         <li>",
"          Provides reliable access for repeat procedures",
"         </li>",
"        </ul>",
"       </td>",
"       <td class=\"indent1\">",
"        More invasive than purely endoscopic techniques",
"       </td>",
"       <td>",
"        RYGB patients with native papilla, or when repeated procedures are anticipated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laparoscopy-assisted ERCP",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Allows use of side viewing duodenoscope and all standard accessories",
"         </li>",
"         <li>",
"          Ability to diagnosis and treat internal hernias",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          More invasive than purely endoscopic techniques",
"         </li>",
"         <li>",
"          Requires significant coordination between surgery and endoscopy teams",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        RYGB patients with native papilla, particularly when internal hernia is suspected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Percutaneous approaches via interventional radiology",
"       </td>",
"       <td class=\"indent1\">",
"        Less invasive than surgical approaches",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Morbidity (pain, external drains)",
"         </li>",
"         <li>",
"          No access to pancreas",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Patients with biliary tract pathology who are poor surgical candidates",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ERCP: endoscopic retrograde cholangiopancreatography; RYGB: Roux-en-Y gastric bypass.",
"    </div>",
"    <div class=\"reference\">",
"     Original figure modified for this publication. Lopes TL, Wilcox CM. Endoscopic retrograde cholangiopancreatography in patients with Roux-en-Y anatomy. Gastroenterol Clin North Am 2010; 39:99. Illustration used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
