var title_f2_43_2748="Features of bleeding disorders";
var content_f2_43_2748=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F77834&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F77834&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical manifestations of bleeding disorders",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Bleeding symptoms",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Bleeding disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Platelet defects (qualitative or quantitative)",
"       </td>",
"       <td class=\"subtitle2\">",
"        Clotting factor deficiencies (eg,&nbsp; factor VIII or factor IX deficiencies)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Overview of bleeding events",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        <p>",
"         Mucocutaneous bleeding",
"         <br/>",
"         &nbsp;&nbsp;&nbsp; (oral cavity, nasal, gastrointestinal, and genitourinary sites)",
"        </p>",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Deep tissue bleeding",
"        <br/>",
"        &nbsp;&nbsp;&nbsp; (including joints and muscles)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Excessive bleeding after minor cuts",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        Not usually",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Petechiae",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"       <td>",
"        Uncommon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ecchymoses",
"       </td>",
"       <td>",
"        Generally small and superficial; may be significant, depending upon the defect or degree of thrombocytopenia",
"       </td>",
"       <td>",
"        May develop large subcutaneous and soft tissue hematomas",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hemarthroses, muscle hematomas",
"       </td>",
"       <td>",
"        Uncommon",
"       </td>",
"       <td>",
"        Common in severe deficiency states or in association with injury in those with mild to moderate deficiency states",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bleeding with invasive procedures, including surgery",
"       </td>",
"       <td>",
"        Often immediate, with degree of bleeding dependent upon the severity of the defect, ranging from none (eg, mild degrees of thrombocytopenia or mild platelet function defect) to mild to severe (eg, Glanzmann thrombasthenia)",
"       </td>",
"       <td>",
"        May be associated either with procedural bleeding or delayed bleeding, depending upon the type and severity of the defect",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
