var title_f2_46_2796="DSM-IV-TR diagnostic criteria for bulimia nervosa";
var content_f2_46_2796=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F70006&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F70006&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    DSM-IV-TR diagnostic criteria for bulimia nervosa",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Recurrent episodes of binge eating. An episode of binge eating is characterized by both of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Eating, in a discrete period of time (eg, within any two-hour period), an amount of food that is definitely larger than most people would eat during a similar period of time and under similar circumstances.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        A sense of lack of control over eating during the episode (eg, a feeling that one cannot stop eating or control what or how much one is eating).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recurrent inappropriate compensatory behavior to prevent weight gain, such as self-induced vomiting; misuse of laxatives, diuretics, enemas,&nbsp;or other medications; fasting; or excessive exercise.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The binge eating and inappropriate compensatory behaviors both occur, on average, at least twice a week for three months.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Self-evaluation is unduly influenced by body shape and weight.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The disturbance does not occur exclusively during episodes of anorexia nervosa.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Specify type:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Purging type: during the current episode of bulimia nervosa, the person has regularly engaged in self-induced vomiting or the misuse of laxatives,&nbsp;diuretics, or enemas.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Nonpurging type: during the current episode of bulimia nervosa, the person has used other inappropriate compensatory behaviors, such as fasting or excessive exercise, but has not&nbsp;regularly engaged in self-induced vomiting or the misuse of laxatives,&nbsp;diuretics, or enemas.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from American Psychiatric Association: Diagnostic and Statistical Manual of Mental Disorders, Fourth Edition, Text Revision. Washington, DC,&nbsp;American Psychiatric Association, 2000, p. 594.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
