var title_f2_46_2797="Management of life-threatening acute respiratory compromise";
var content_f2_46_2797=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F57278&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F57278&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Management of life-threatening causes of acute respiratory compromise in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Maneuver",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Complete upper airway obstruction*",
"       </td>",
"       <td>",
"        Needle cricothyrotomy",
"       </td>",
"       <td>",
"        This is a temporizing measure that can provide oxygenation, but not ventilation",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Foreign body",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Back blows/chest thrusts (&lt;1 year of age)",
"       </td>",
"       <td>",
"        Maneuvers should only be used for patients who are unable to phonate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abdominal thrusts (&ge;1 year of age)",
"       </td>",
"       <td>",
"        Maneuvers should only be used for patients who are unable to phonate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Manual removal with finger sweep",
"       </td>",
"       <td>",
"        Perform this maneuver only when foreign body is visible in the oropharynx",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laryngoscopy and removal with Magill forceps",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Laryngospasm",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        Positive pressure with a ventilation bag and tight fitting mask",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Soft tissue upper airway obstruction",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        Head tilt/chin lift",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Jaw thrust",
"       </td>",
"       <td>",
"        Use for patients who may have cervical spine injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nasopharyngeal airway",
"       </td>",
"       <td>",
"        May be tolerated by a conscious patient",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oropharyngeal airway",
"       </td>",
"       <td>",
"        Use only in an unconscious patient",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Respiratory failure",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        Bag-mask ventilation",
"       </td>",
"       <td>",
"        Suspect upper airway obstruction if unable to ventilate with proper size equipment and technique",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Endotracheal intubation",
"        <sup>",
"         &loz;",
"        </sup>",
"       </td>",
"       <td>",
"        Use for patient who requires more than a few minutes of assisted ventilation or those with complete subglottic upper airway obstruction",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Tension pneumothorax",
"       </td>",
"       <td>",
"        Needle thoracentesis",
"       </td>",
"       <td>",
"        Patients will require chest tube placement following emergent decompression",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Cardiac tamponade",
"       </td>",
"       <td>",
"        Pericardiocentesis",
"        <sup>",
"         &sect;",
"        </sup>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * See topic on \"Needle cricothyroidotomy with percutaneous transtracheal ventilation\".",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      See topic on \"Emergent evaluation of acute upper airway obstruction in children\", section on 'Suspected foreign body'.",
"      <br>",
"       &Delta; See topic on \"Basic airway management in children\".",
"       <br>",
"        <span class=\"lozenge\">",
"         &loz;",
"        </span>",
"        See topic on \"Emergent endotracheal intubation in children\".",
"        <br>",
"         &sect; See topic on \"Emergency pericardiocentesis\".",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
