var title_f2_48_2828="GOLD COPD Table 2";
var content_f2_48_2828=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F56868&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F56868&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Table 2. Classification of severity*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Characteristics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        0: At Risk",
"       </td>",
"       <td>",
"        Normal spirometry",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic symptoms (cough, sputum production)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        I: Mild COPD",
"       </td>",
"       <td>",
"        FEV1/FVC &lt;70 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        FEV1 &ge;80 percent predicted with or without chronic symptoms (cough, sputum production)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        II: Moderate COPD",
"       </td>",
"       <td>",
"        FEV1/FVC &lt;70 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        50 percent &le;FEV1 &lt;80 percent predicted with or without chronic symptoms (cough, sputum production)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        III: Severe COPD",
"       </td>",
"       <td>",
"        FEV1/FVC &lt;70 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        30 percent &le;FEV1 &lt;50 percent predicted with or without chronic symptoms (cough, sputum production)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        IV: Very Severe COPD",
"       </td>",
"       <td>",
"        FEV1/FVC &lt;70 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        FEV1 &lt;30 percent predicted or FEV1&lt;50 percent predicted plus chronic respiratory failure",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     FEV1: forced expiratory volume in one second; FVC: forced vital capacity; respiratory failure: arterial partial pressure of oxygen (PaO2) less than 8.0 kPa (60 mm Hg) with or without arterial partial pressure of CO2 (PaCO2) greater than 6.7 kPa (50 mm Hg) while breathing air at sea level.",
"     <br>",
"      * Classification based on postbronchodilator FEV1.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Global Strategy for the Diagnosis, Management and Prevention of Chronic Obstructive Pulmonary Disease: Executive Summary (Updated 2005). Reproduced with permission from the Global Initiative for Chronic Obstructive Lung Disease (GOLD), file://www.goldcopd.org.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
