" <div id=\"innerOutline\">",
"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H1\">",
"      INTRODUCTION",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H2\">",
"      MEASUREMENT OF RENAL WATER EXCRETION",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H3\">",
"      Physiologic Factors Affecting CH2O",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H4\">",
"      MEASUREMENT OF RENAL WATER REABSORPTION",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H5\">",
"      Electrolyte-free water reabsorption",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H6\">",
"      Clinical example",
"     </a>",
