var title_f2_52_2892="Vaughan Williams classification of antiarrhythmic drugs";
var content_f2_52_2892=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F56600&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F56600&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Vaughan Williams classification of antiarrhythmic drugs",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IA - slows conduction velocity (less than class IC) and prolongs action potential duration (APD)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Disopyramide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Procainamide&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Quinidine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IB - has no effect on conduction velocity and may shorten APD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Lidocaine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Mexiletine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Phenytoin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IC - slows conduction and may prolong APD (mild)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Flecainide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Propafenone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class II - blocks beta adrenergic receptors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Beta blockers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class III - prolongs APD and has no effect on conduction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Amiodarone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Dofetilide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Ibutilide&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Sotalol*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Dronedarone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IV - calcium channel blockers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Nondihydropyridine calcium channel blockers (verapamil and dilitiazem)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     APD: action potential duration.",
"     <br>",
"      &bull; Available only in intravenous formulations.",
"      <br>",
"       * I-sotalol has beta blocking and class III activities; d-sotalol is a pure class III agent.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
