var title_f2_56_2956="ACC AHA serial assessment HF";
var content_f2_56_2956=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F50551&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F50551&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Serial clinical assessment of patients with heart failure (HF)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I - There is evidence and/or general agreement that the following serial clinical assessments should be performed at each visit in patients with HF:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; The ability to perform routine and desired activities of daily living.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Volume status and weight.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Careful history of current use of alcohol, tobacco, illicit drugs, \"alternative therapies,\" and chemotherapy drugs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Diet and sodium intake.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIa - The weight of evidence or opinion is in favor of the usefulness of the following serial clinical assessment in patients with HF:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Repeat measurements of left ventricular ejection fraction and the severity of structural remodeling in patients who have had a change in clinical status or who have experienced or recovered from a clinical event or received treatment that might have had a significant effect on cardiac function.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIb - The weight of evidence or opinion is less well established for the usefulness of the following serial clinical assessment of patients with HF:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Serial measurements of serum B-type natriuretic peptide (BNP) to guide therapy.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Hunt SA, Abraham WT, Chin MH, et al. ACC/AHA 2005 Guideline Update for the Diagnosis and Management of Chronic Heart Failure in the Adult: a report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing Committee to Update the 2001 Guidelines for the Evaluation and Management of Heart Failure): developed in collaboration with the American College of Chest Physicians and the International Society for Heart and Lung Transplantation: endorsed by the Heart Rhythm Society. Circulation 2005; 112:e154.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
