var title_f2_60_3020="Rx MRSA skin infections in children";
var content_f2_60_3020=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F63973&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F63973&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Dosing for selected oral antimicrobials for suspected methicillin-resistant Staphylococcus aureus skin and soft-tissue infections in children older than 30 days",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Trimethoprim-sulfamethoxazole (TMP-SMX)*",
"       </td>",
"       <td>",
"        8 to 12 mg/kg per day of TMP, 40 to 60 mg/kg per day of SMX in 2 doses; maximum daily dose 320 mg of TMP, 1.6 g SMX",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clindamycin",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        30 to 40 mg/kg per day in 3 to 4 doses; maximum daily dose 600 mg to 1.8 g",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Doxycycline*",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        2 to 4 mg/kg per day in 1 to 2 doses; maximum daily dose 100 to 200 mg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Linezolid",
"       </td>",
"       <td>",
"        <p>",
"         &lt;12 years: 30 mg/kg per day in 3 doses",
"        </p>",
"        <p>",
"         &ge;12 years: 600 mg twice per day",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Does not have activity against",
"     <em>",
"      Streptococcus pyogenes",
"     </em>",
"     (group A streptococcus).",
"     <br>",
"      <font class=\"bullet\">",
"       &bull;",
"      </font>",
"      If the prevalence of clindamycin resistance in the community is low. D testing required to evaluate for inducible resistance (see text).",
"      <br>",
"       &Delta; Should not be used in children &lt;8 years of age.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        American Academy of Pediatrics. Antibacterial drugs for pediatric patients beyond the newborn period. In: Red Book: 2012 Report of the Committee on Infectious Diseases, 29th ed, Pickering LK (Ed), American Academy of Pediatrics, Elk Grove Village, IL 2012. p.810.",
"       </li>",
"       <li>",
"        Liu C, Bayer A, Cosgrove SE, et, al. Clinical practice guidelines by the Infectious Diseases Society of America for the treatment of methicillin-resistant Staphylococcus aureus infections in adults and children. Clin Infect Dis 2011; 52:e18.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
