var title_f2_7_2172="MSKCC progn strat IFN response";
var content_f2_7_2172=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F61752&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F61752&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Memorial Sloan Kettering prognostic stratification for response to interferon-alfa (INFa) for advanced RCC",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Prognostic factors*",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Median progression-free survival",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Median overall survival",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"3\">",
"        Overall survival (percent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        One year",
"       </td>",
"       <td class=\"subtitle2\">",
"        Two year",
"       </td>",
"       <td class=\"subtitle2\">",
"        Three year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0 (low-risk)",
"       </td>",
"       <td>",
"        8.3 months",
"       </td>",
"       <td>",
"        30 months",
"       </td>",
"       <td>",
"        83",
"       </td>",
"       <td>",
"        55",
"       </td>",
"       <td>",
"        45",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1 to 2 (intermediate-risk)",
"       </td>",
"       <td>",
"        5.1 months",
"       </td>",
"       <td>",
"        14 months",
"       </td>",
"       <td>",
"        58",
"       </td>",
"       <td>",
"        31",
"       </td>",
"       <td>",
"        17",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 to 5 (high-risk)",
"       </td>",
"       <td>",
"        2.5 months",
"       </td>",
"       <td>",
"        5 months",
"       </td>",
"       <td>",
"        20",
"       </td>",
"       <td>",
"        6",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Poor prognostic factors:",
"     <br>",
"      <ul>",
"       <li>",
"        Karnofsky Performance Status (KPS) &lt;80 percent",
"       </li>",
"       <li>",
"        Serum lactate dehydrogenase (LDH) &gt;1.5 x upper limit of normal (ULN)",
"       </li>",
"       <li>",
"        Serum hemoglobin less than normal",
"       </li>",
"       <li>",
"        Serum calcium &gt;10 mg/dl",
"       </li>",
"       <li>",
"        Time from initial diagnosis to IFNa of &lt;1 year",
"       </li>",
"      </ul>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Motzer RJ, Bacik J, Murphy BA, et al. Interferon-alfa as a comparative treatment for clinical trials of new therapies against advanced renal cell carcinoma. J Clin Oncol 2002; 20:289.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
