var title_f2_7_2173="Molecular test transverse myelitis";
var content_f2_7_2173=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F59579&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F59579&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Molecular tests for infectious causes of transverse myelitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Etiology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Availability*",
"       </td>",
"       <td class=\"subtitle1\">",
"        When inclusion of NAAT is indicated",
"       </td>",
"       <td class=\"subtitle1\">",
"        Preferred testing method(s)&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Bacteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mycoplasma spp",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        When clinically suspected",
"       </td>",
"       <td>",
"        NAAT, serology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mycobacterium tuberculosis&Delta;",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        Potentially",
"       </td>",
"       <td>",
"        AFB stain, culture, histologic exam",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Viruses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cytomegalovirus (CMV)",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        When clinically suspected",
"       </td>",
"       <td>",
"        NAAT, preferably quantitative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Epstein Barr virus (EBV)",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        When clinically suspected",
"       </td>",
"       <td>",
"        NAAT, preferably quantitative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Enteroviruses",
"       </td>",
"       <td>",
"        Widespread",
"       </td>",
"       <td>",
"        When clinically suspected",
"       </td>",
"       <td>",
"        NAAT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Herpes simplex virus (HSV)",
"       </td>",
"       <td>",
"        Widespread",
"       </td>",
"       <td>",
"        When clinically suspected",
"       </td>",
"       <td>",
"        NAAT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mumps",
"       </td>",
"       <td>",
"        Limited",
"       </td>",
"       <td>",
"        Rarely",
"       </td>",
"       <td>",
"        History, viral culture, serology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Varicella zoster virus (VZV)",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        When clinically suspected",
"       </td>",
"       <td>",
"        NAAT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        West Nile virus&loz;",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        Rarely (unless immunodeficient)",
"       </td>",
"       <td>",
"        Serology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Spirochetes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Treponema pallidum (syphilis)",
"       </td>",
"       <td>",
"        Limited",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        Serology, CSF VDRL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Parasites",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Schistosomiasis",
"       </td>",
"       <td>",
"        Limited",
"       </td>",
"       <td>",
"        Rarely",
"       </td>",
"       <td>",
"        Serology, histologic exam",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PPV: positive predictive value; CDC: Centers for Disease Control and Prevention; NAAT: nucleic acid amplification testing; CSF: cerebrospinal fluid; AFB: acid fast bacilli.",
"     <br>",
"      * Widespread: readily available; Yes: consistently available, but not yet common; Limited: one or two specialized laboratories (eg, Centers for Disease Control and Prevention (CDC) or research groups).",
"      <br>",
"       &bull; Test on CSF except where noted.",
"       <br>",
"        &Delta; Limited experience, presumably poor sensitivity, high PPV.",
"        <br>",
"         &loz; Virus is usually cleared from blood and CSF prior to symptom onset.",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
