var title_f2_9_2205="Indication contraind LVRS";
var content_f2_9_2205=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F69501&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F69501&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Potential indications and contraindications for LVRS",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Parameter",
"      </td>",
"      <td class=\"subtitle1\">",
"       Indications",
"      </td>",
"      <td class=\"subtitle1\">",
"       Contraindications",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"4\">",
"       Clinical",
"      </td>",
"      <td>",
"       Age &lt;75 years",
"      </td>",
"      <td>",
"       Age &ge;75 years",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ex-smoker (&gt;6 months)",
"      </td>",
"      <td>",
"       Current smoking",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Clinical picture consistent with emphysema",
"      </td>",
"      <td>",
"       Surgical constraints (eg, previous thoracic procedure, pleurodesis, chest wall deformity)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Dyspnea despite maximal medical therapy and pulmonary rehabilitation",
"      </td>",
"      <td>",
"       Pulmonary hypertension (PA systolic &gt;45 mmHg, PA mean &gt;35 mmHg)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"5\">",
"       Comorbid illness*",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Clinically significant bronchiectasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Clinically significant coronary heart disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Heart failure with an ejection fraction &lt;45 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Uncontrolled hypertension",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Obesity&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"4\">",
"       Physiology",
"      </td>",
"      <td>",
"       FEV1 after bronchodilator &lt;45 percent predicted",
"      </td>",
"      <td>",
"       FEV1 &le;20 percent predicted with either DLCO &le;20 percent predicted or homogeneous emphysema",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hyperinflation (TLC &gt;100 percent predicted, RV &gt;150 percent)",
"      </td>",
"      <td>",
"       PaO2 &le;45 mmHg on room air",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Post rehabilitation 6-minute walk distance &gt;140 meters",
"      </td>",
"      <td>",
"       PaCO2 &ge;60 mmHg",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Low post rehabilitation maximal achieved cycle ergometry watts&Delta;",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"4\">",
"       Imaging",
"      </td>",
"      <td>",
"       Chest radiograph - hyperinflation",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       HRCT confirming severe emphysema, ideally with upper lobe predominance",
"      </td>",
"      <td>",
"       Homogeneous emphysema with FEV1 &le;20 percent predicted",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Significant pleural or interstitial changes on HRCT",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Nonupper lobe predominant emphysema and high post rehabilitation maximal achieved cycle ergometry watts&loz;",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     FEV1: forced expiratory volume in one second; TLC: total lung capacity; RV: residual volume; PaO2: arterial partial pressure of oxygen; PaCO2: arterial partial pressure of carbon dioxide; HRCT: high resolution computed tomography.",
"     <br>",
"      * Comorbid illnesses may be considered relative contraindications depending on degree of severity.",
"      <br>",
"       &bull; The NETT trial required a body mass index less than 31.1 kg/m2 for men and less than 32.3 kg/m2 for women. It is not known whether more severe obesity would adversely affect perioperative risk.",
"       <br>",
"        &Delta; Low post rehabilitation maximal achieved cycle ergometry watts (W) is defined as at or below the sex specific 40th percentile (25 W for women and 40 W for men).",
"        <br>",
"         &loz; High post rehabilitation maximal achieved cycle ergometry watts (W) is defined as above the sex specific 40th percentile (25 W for women and 40 W for men). This is a relative contraindication.",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: www.thoracic.org/COPD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
