" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of Niemann-Pick disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/0/20489/contributors\">",
