" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations, diagnosis and prognosis of Crohn's disease in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/0/20490/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/0/20490/contributors\">",
"     Mark A Peppercorn, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/0/20490/contributors\">",
