" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pelvic examination under anesthesia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/1/20499/contributors\">",
