var title_f20_1_20507="Firearm safety tips";
var content_f20_1_20507=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F63269&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F63269&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Firearm safety tips",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Any gun is a potential danger to children in the home; the risks must be seriously considered.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Firearms should be stored unloaded, locked up, and out of children's reach.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ammunition should be stored in a separate, locked location.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Quality gun locks, lock boxes, or gun safes should be used on every firearm. When correctly installed, gun locks prevent firearms from being discharged unless the lock is removed.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Gun storage keys and lock combinations should be kept hidden in a location separate from the guns.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Parents who have guns should take a course in using, maintaining, and storing them safely.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Parents should talk to their children about the dangers of guns and teach their children never to touch or play with a gun.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Children should be taught to tell an adult if they find a gun, or, if no adult is present, to call 911 or the local emergency number.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Check with neighbors, friends, relatives, and adults in other homes where the child visits to make sure that if they have firearms in the home, they follow safe storage practices.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: The National Safe Kids Campaign. Firearms: Protecting your family. Available at: www.usa.safekids.org/tier3_cd.cfm?content_item_id=315&amp;folder_id=172 (Accessed on April 28, 2008).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
