" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Left colectomy: Open technique",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/16/20744/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/16/20744/contributors\">",
"     Anthony MacLean, MD, FRCSC, FACS",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/16/20744/contributors\">",
"     W Donald Buie, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/16/20744/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/16/20744/contributors\">",
"     Martin Weiser, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/16/20744/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/16/20744/contributors\">",
"     Rosemary B Duda, MD, MPH, FACS",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
