"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/17/20762/contributors\">",
"     Rebecca F Connor, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?20/17/20762/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
