var title_f20_17_20763="Ultrasound features associated with thyroid cancer risk";
var content_f20_17_20763=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F68199&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F68199&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ultrasound features associated with thyroid cancer risk",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Ultrasonographic features that are associated with an increased risk of thyroid cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypoechoic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Microcalcifications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        \"Twinkling\" on B-flow imaging",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Central vascularity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Irregular margins",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Incomplete halo",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nodule is taller than wide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Documented enlargement of a nodule",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Ultrasonographic features that are associated with a low risk of thyroid cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperechoic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Large, coarse calcifications (except medullary cancer)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peripheral vascularity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Resembles puff or Napoleon pastry",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Spongiform appearance&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Comet-tail shadowing",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
