var script_f20_19_20786=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function OsmolalGap_SI_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.OsmolalGap_SI_form){",
"",
"",
"doCalc = true;",
"if (Serum_Na_param.value.indexOf(',') >= 0){ Serum_Na_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Serum_Na_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Serum_Na_unit.options[Serum_Na_unit.selectedIndex].value.split('|');",
"Serum_Na = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Serum_Glucose_param.value.indexOf(',') >= 0){ Serum_Glucose_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Serum_Glucose_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Serum_Glucose_unit.options[Serum_Glucose_unit.selectedIndex].value.split('|');",
"Serum_Glucose = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Serum_BUN_param.value.indexOf(',') >= 0){ Serum_BUN_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Serum_BUN_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Serum_BUN_unit.options[Serum_BUN_unit.selectedIndex].value.split('|');",
"Serum_BUN = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Serum_Ethanol_param.value.indexOf(',') >= 0){ Serum_Ethanol_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Serum_Ethanol_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Serum_Ethanol_unit.options[Serum_Ethanol_unit.selectedIndex].value.split('|');",
"Serum_Ethanol = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Measured_Osm_param.value.indexOf(',') >= 0){ Measured_Osm_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Measured_Osm_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Measured_Osm_unit.options[Measured_Osm_unit.selectedIndex].value.split('|');",
"Measured_Osm = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"Calculated_Osm =  (Serum_Na * 2) + Serum_Glucose + Serum_BUN  + (Serum_Ethanol * 1.25);",
"",
"unit_parts = Calculated_Osm_unit.options[Calculated_Osm_unit.selectedIndex].value.split('|');",
"if (doCalc) Calculated_Osm_param.value = fixDP((Calculated_Osm - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"Osmolal_Gap =  Measured_Osm - Calculated_Osm;",
"",
"unit_parts = Osmolal_Gap_unit.options[Osmolal_Gap_unit.selectedIndex].value.split('|');",
"if (doCalc) Osmolal_Gap_param.value = fixDP((Osmolal_Gap - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.OsmolalGap_SI_form){",
"",
"if (Serum_Na_param.value && Serum_Na < 50) {",
"Serum_Na = 0;",
"Serum_Na_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Serum Na is 50 mmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Serum_Na_param.value && Serum_Na > 200) {",
"Serum_Na_param.value = \"\";",
"clrResults();",
"Serum_Na = 0;",
"doCalc = false;",
"alert(\"The maximum value for Serum Na is 200 mmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Serum_Glucose_param.value && Serum_Glucose < 0) {",
"Serum_Glucose = 0;",
"Serum_Glucose_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Serum Glucose is 0 mmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Serum_Glucose_param.value && Serum_Glucose > 83) {",
"Serum_Glucose_param.value = \"\";",
"clrResults();",
"Serum_Glucose = 0;",
"doCalc = false;",
"alert(\"The maximum value for Serum Glucose is 83 mmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Serum_BUN_param.value && Serum_BUN < 0) {",
"Serum_BUN = 0;",
"Serum_BUN_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Serum BUN is 0 mmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Serum_BUN_param.value && Serum_BUN > 54) {",
"Serum_BUN_param.value = \"\";",
"clrResults();",
"Serum_BUN = 0;",
"doCalc = false;",
"alert(\"The maximum value for Serum BUN is 54 mmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Serum_Ethanol_param.value && Serum_Ethanol < 0) {",
"Serum_Ethanol = 0;",
"Serum_Ethanol_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Serum Ethanol is 0 mmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Serum_Ethanol_param.value && Serum_Ethanol > 435) {",
"Serum_Ethanol_param.value = \"\";",
"clrResults();",
"Serum_Ethanol = 0;",
"doCalc = false;",
"alert(\"The maximum value for Serum Ethanol is 435 mmol/L.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.OsmolalGap_SI_form){",
"",
"Calculated_Osm_param.value = '';",
"Osmolal_Gap_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
