var title_f20_23_20859="Long duration chronic daily headache";
var content_f20_23_20859=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F52077&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F52077&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Chronic daily headaches of long duration",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td colspan=\"2\">",
"        Chronic daily headaches of long duration are characterized by headache duration &ge;4 hours a day with headaches occurring &ge;15 days per month for &gt;3 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Chronic tension-type headache:",
"        </strong>",
"       </td>",
"       <td>",
"        Bilateral, gradual onset, muscle tenderness common",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Transformed migraine:",
"        </strong>",
"       </td>",
"       <td>",
"        Variable location, typically of gradual onset, migraine features present, medication overuse common",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         New daily persistent headache:",
"        </strong>",
"       </td>",
"       <td>",
"        Sudden onset, usually bilateral, typically unresponsive to treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Hemicrania continua:",
"        </strong>",
"       </td>",
"       <td>",
"        Strictly unilateral, unremitting without pain-free intervals but with variable intensity, autonomic features ipsilateral to the side of pain, responsive to indomethacin",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Silberstein SD, Lipton RB, Sliwinski M. Classification of daily and near-daily headaches: field trial of revised HIS criteria. Neurology 1996; 47:871.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
