var title_f20_23_20860="Allergy causes and symptoms PI";
var content_f20_23_20860=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F86626&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F86626&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes and symptoms of common allergies",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type of allergy",
"       </td>",
"       <td class=\"subtitle1\">",
"        Caused by",
"       </td>",
"       <td class=\"subtitle1\">",
"        Symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Seasonal allergies (also called hay fever)",
"       </td>",
"       <td>",
"        Pollen from trees, grasses, or weeds",
"       </td>",
"       <td rowspan=\"5\">",
"        <ul>",
"         <li>",
"          Stuffy nose, runny nose, or sneezing a lot",
"         </li>",
"         <li>",
"          Itchy or red eyes, sore throat, or itching of the throat or ears",
"         </li>",
"         <li>",
"          Waking up at night or trouble sleeping, which can lead to feeling tired during the day",
"         </li>",
"         <li>",
"          Asthma symptoms (wheezing or noisy breathing, coughing, a tight feeling in the chest, and shortness of breath)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mold spores that grow when the weather is humid, wet, or damp",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Indoor allergies",
"       </td>",
"       <td>",
"        Mold spores",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Animals, such as cats and dogs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Insects, such as dust mites and cockroaches",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Insect allergies",
"       </td>",
"       <td>",
"        Stings from honeybees, yellow jackets, hornets and wasps, fire ants",
"       </td>",
"       <td rowspan=\"4\">",
"        <ul>",
"         <li>",
"          Red, hot, skin especially on your face (flushing)",
"         </li>",
"         <li>",
"          Red, raised patches of skin that are very itchy (hives)",
"         </li>",
"         <li>",
"          Swelling of the face, eyelids, mouth, tongue, hands, and feet (angioedema)",
"         </li>",
"         <li>",
"          Asthma symptoms (wheezing or noisy breathing, coughing, a tight feeling in the chest, and shortness of breath)",
"         </li>",
"         <li>",
"          Nausea and vomiting",
"         </li>",
"         <li>",
"          Feeling lightheaded or like you might pass out",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Food allergies",
"       </td>",
"       <td>",
"        Milk and foods that contain milk such as ice cream or butter (called dairy foods); eggs, wheat, soy, peanuts, tree nuts, (such as almonds or cashews); fish, shellfish (such as shrimp or oysters)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Medicine allergies",
"       </td>",
"       <td>",
"        Penicillin and related medicines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Latex allergy",
"       </td>",
"       <td>",
"        Gloves and other medical supplies that contain latex; condoms, balloons",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
