" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Cryoablation of lung tumors",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/26/20900/contributors\">",
