var title_f20_26_20908="Risk stratification for syncope";
var content_f20_26_20908=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F78397&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F78397&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk stratification for syncope",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <strong>",
"         SHORT-TERM HIGH RISK CRITERIA WHICH REQUIRE PROMPT HOSPITALIZATION OR INTENSIVE EVALUATION",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Severe structural or coronary artery disease",
"        </strong>",
"        (heart failure, low LVEF, or previous myocardial infarction)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Clinical or ECG features suggesting arrhythmic syncope",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Syncope during exertion or supine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Palpitations at the time of syncope",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Family history of SCD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Non-sustained VT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Bifascicular-block (LBBB or RBBB combined with left anterior or left posterior fascicular block) or other intraventricular conduction abnormalities with QRS duration &ge;120 ms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Inadequate sinus bradycardia (&lt;50 bpm) or sinoartrial block in absence of negative chronotropic medications or physical training",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Pre-excited QRS complex",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Prolonged or short QT interval",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - RBBB pattern with ST-elevation in leads V1-V3 (Brugada pattern)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Negative T waves in right precordial leads, epsilon waves, and ventricular late potentials suggestive of ARVC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Important co-morbidities",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Severe anaemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Electrolyte disturbance",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ARVC: arrhythmogenic right ventricular cardiomyopathy; bpm: beats per minute; LBBB: left bundle branch block; LVEF: left ventricular ejection fraction; RBBB: right bundle branch block; SCD: sudden cardiac death; VT: ventricular tachycardia.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: European Heart Rhythm Association (EHRA), Heart Failure Association (HFA), Heart Rhythm Society (HRS), et al. Guidelines for the diagnosis and management of syncope (version 2009): the Task Force for the Diagnosis and Management of Syncope of the European Society of Cardiology (ESC). Eur Heart J 2009; 30:2631. Copyright &copy; 2009 Oxford University Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
