var title_f20_26_20909="Methamphetamine intoxication in adults - Rapid overview";
var content_f20_26_20909=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F73681&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F73681&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Methamphetamine intoxication in adults: Rapid overview",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <span style=\"color: #ff0000;\">",
"         To obtain emergent consultation with a medical toxicologist, call the United States Poison Control Network at 1-800-222-1222, or access the World Health Organization's list of international poison centers (",
"        </span>",
"        <a href=\"file://www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html\" target=\"_blank\">",
"         www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html",
"        </a>",
"        <span style=\"color: #ff0000;\">",
"         ).",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        General",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May be abused by nasal, oral, or intravenous route",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rapid onset of symptoms with long (~24 hours) duration of action",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History may be difficult to obtain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe agitation can occur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Sympathomimetic toxidrome frequently present:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; CNS stimulation (agitation, delirium or acute psychosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Dilated pupils (mydriasis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Diaphoresis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperthermia (variable; may be severe)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Laboratory analysis:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Routine testing in poisoned patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Fingerstick glucose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; ECG to assess for conduction system impairment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Acetaminophen and salicylate levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Pregnancy test in women of childbearing age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Basic electrolytes, BUN, creatinine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Metabolic acidosis, acute renal failure, and hyperkalemia may be seen in severe toxicity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Creatinine phosphokinase, serum lactate, aminotransferases, coagulation studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Agitation (control with aggressive sedation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Benzodiazepines are first line therapy; may need large doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; For severe agitation:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        - Lorazepam, 4 mg IV, may repeat every 8 to 10 minutes, titrate to sedation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        - Diazepam, 5 to 10 mg IV, may repeat every 8 to 10 minutes, titrate to sedation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        - If lacks IV access, midazolam 5 to 10 mg intramuscularly, may repeat every 10 minutes, titrate to sedation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Consider other agents (see text) in cases of benzodiazepine failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paralysis and tracheal intubation may be necessary; succinylcholine should",
"        <strong>",
"         not",
"        </strong>",
"        be used for rapid sequence intubation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Critical hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Benzodiazepines are first line therapy (see dosing above)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Nitroprusside: start at 0.25 to 0.5 mcg/kg/min; titrate to effect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Phentolamine: 2-5 mg IV, may repeat; titrate to effect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull;",
"        <strong>",
"         AVOID BETA BLOCKING AGENTS, INCLUDING LABETALOL",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Hyperthermia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Sedation with benzodiazepines (see dosing above);",
"        <strong>",
"         DO NOT",
"        </strong>",
"        give antipyretics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Evaporative cooling measures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Cooling blankets",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Neuromuscular paralysis in severe cases",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
