var title_f20_29_20957="Immunologic sys drug rxns";
var content_f20_29_20957=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F77196&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F77196&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison of systemic immunologic drug reactions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"6\" width=\"16%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type of immunologic drug reaction",
"       </td>",
"       <td class=\"subtitle1\">",
"        Incidence",
"       </td>",
"       <td class=\"subtitle1\">",
"        Signs and symptoms",
"       </td>",
"       <td class=\"subtitle1\">",
"        Timing",
"       </td>",
"       <td class=\"subtitle1\">",
"        Common culprit drugs",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnostic testing available?",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         IgE, mast cell and basophil mediated",
"        </p>",
"        <p>",
"         Type I Gell and Coombs",
"        </p>",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"       <td>",
"        Urticaria/angioedema, bronchospasm, laryngeal edema, nausea, vomiting, hypotension, shock",
"       </td>",
"       <td>",
"        <p>",
"         Immediate: Seconds to 1 hour after last dose",
"        </p>",
"        <p>",
"         May be slightly more delayed if drug slowly absorbed",
"        </p>",
"        <p>",
"         Faster and more severe upon re-exposure",
"        </p>",
"       </td>",
"       <td>",
"        Beta-lactam antibiotics (penicillins and cephalosporins), muscle relaxants, foreign proteins (eg, cetuximab, rituximab), platinum-based chemotherapy (eg, carboplatin, cisplatin)",
"       </td>",
"       <td>",
"        <p>",
"         YES",
"        </p>",
"        <p>",
"         At time of reaction: Serum tryptase",
"        </p>",
"        <p>",
"         Later: Skin prick and intradermal testing",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Antibody-mediated (IgG and IgM) cell destruction",
"        </p>",
"        <p>",
"         Type II Gell and Coombs",
"        </p>",
"       </td>",
"       <td>",
"        Uncommon",
"       </td>",
"       <td>",
"        <p>",
"         Hemolytic anemia",
"        </p>",
"        <p>",
"         Thrombocytopenia",
"        </p>",
"        <p>",
"         Neutropenia or agranulocytosis",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Delayed: Days to weeks after beginning therapy",
"        </p>",
"        <p>",
"         May occur months into long-term treatments",
"        </p>",
"        <p>",
"         Faster upon re-exposure",
"        </p>",
"       </td>",
"       <td>",
"        Beta-lactam antibiotics, NSAIDs, quinidine, methyldopa, ticlopidine",
"       </td>",
"       <td>",
"        <p>",
"         YES",
"        </p>",
"        <p>",
"         Direct Coombs",
"        </p>",
"        <p>",
"         Tests for anti-platelet antibodies",
"        </p>",
"        <p>",
"         Tests for anti-neutrophil antibodies",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Immune complex-mediated tissue damage",
"        </p>",
"        <p>",
"         Type III Gell and Coombs",
"        </p>",
"       </td>",
"       <td>",
"        Uncommon",
"       </td>",
"       <td>",
"        <p>",
"         Serum sickness, serum-sickness like reactions",
"        </p>",
"        <p>",
"         Vasculitis (eg, cutaneous)",
"        </p>",
"        <p>",
"         Drug fever",
"        </p>",
"        <p>",
"         Acute glomerulonephritis",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Delayed: Weeks to months after beginning therapy",
"        </p>",
"        <p>",
"         Faster on re-exposure",
"        </p>",
"       </td>",
"       <td>",
"        Beta-lactam antibiotics, sulfonamide antibiotics, sirolimus/tacrolimus",
"       </td>",
"       <td>",
"        <p>",
"         No direct diagnostic testing available. Diagnosis is based on patterns of clinical and laboratory findings.",
"        </p>",
"        <p>",
"         Biopsy of affected tissue is sometimes helpful",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Mediated by T cells",
"        </p>",
"        <p>",
"         +/- other cell types (esp eosinophils, neutrophils)",
"        </p>",
"        <p>",
"         Type IV Gell and Coombs",
"        </p>",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"       <td>",
"        <p>",
"         Prominent skin findings +/- other organ involvement: Exanthematous (morbilliform) eruption",
"        </p>",
"        <p>",
"         DRESS/DiHS",
"        </p>",
"        <p>",
"         Acute generalized exanthematous pustulosis (AGEP)",
"        </p>",
"        <p>",
"         Exfoliative dermatoses (SJS/TEN)",
"        </p>",
"       </td>",
"       <td>",
"        Delayed at least 24 to 48 hours after beginning therapy and may begin weeks to months after treatment began",
"       </td>",
"       <td>",
"        Beta-lactam antibiotics, sulfonamide antibiotics and sulfasalazine, minocycline, phenytoin, carbamazepine, lamotrigine, allopurinol, abacavir, nevirapine",
"       </td>",
"       <td>",
"        <p>",
"         VARIABLE",
"        </p>",
"        <p>",
"         Although testing is not standardized and is considered investigational in most cases, some centers perform skin and in vitro testing for type IV reactions. For example, DiHS/DRESS reactions can be evaluated with in vitro lymphocyte activation, and tests have good specificity.",
"        </p>",
"        <p>",
"         ANY form of re-exposure to the culprit drug is CONTRAINDICATED in patients with past severe or exfoliative reactions, including DRESS/DiHS and SJS/TEN",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unclassified",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Drug-induced lupus",
"        </p>",
"        <p>",
"         Fixed drug eruption",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Patch testing at affected area may be useful in fixed drug eruption",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     DRESS/DiHS: drug rash with eosinophilia and systemic symptoms/drug-induced hypersensitivity syndrome.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
