" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Genetics, clinical features, and diagnosis of Marfan syndrome and related disorders",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/3/20538/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/3/20538/contributors\">",
"     Michael J Wright, MBChB, MSc",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/3/20538/contributors\">",
"     Heidi M Connolly, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/3/20538/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/3/20538/contributors\">",
"     Catherine M Otto, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/3/20538/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/3/20538/contributors\">",
"     Susan B Yeon, MD, JD, FACC",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?20/3/20538/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
