" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment and prognosis of pediatric multiple sclerosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/32/21000/contributors\">",
