" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Dynamic parameters for intraoperative fluid therapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Systolic pressure variations",
"       </td>",
"       <td>",
"        Easy to manually calculate",
"       </td>",
"       <td>",
"        Depends on diastolic pressure and on changes in pleural pressure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Pulse pressure variations",
"       </td>",
"       <td rowspan=\"2\">",
"        Directly related to stroke volume variations",
"       </td>",
"       <td>",
"        Not easy to manually calculate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Need specific device for continuous display",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stroke volume variations",
"       </td>",
"       <td>",
"        Accurate analysis despite multiple extrasystoles",
"       </td>",
"       <td>",
"        Need specific device",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
